function next_timeline() {
  document.querySelectorAll(".next-timeline").forEach((nextTimeline) => {
    var items = nextTimeline.querySelectorAll(".next-timeline--item");
    items.forEach((item) => {
      var enableLeftFade = nextTimeline.getAttribute("data-enable-left-fade");
      var enableCenterFade = nextTimeline.getAttribute(
        "data-enable-center-fade"
      );
      var enableRightFade = nextTimeline.getAttribute("data-enable-right-fade");
      var leftSection = item.querySelector(".next-timeline--left-section");
      var centerSection = item.querySelector(".next-timeline--center-section");
      var rightSection = item.querySelector(".next-timeline--right-section");
      if (leftSection) {
        var leftStickyWrapperFade = leftSection.querySelectorAll(
          ".next-timeline--sticky-wrapper > *"
        );
      }
      if (centerSection) {
        var centerStickyWrapperFade = centerSection.querySelectorAll(
          ".next-timeline--sticky-wrapper > *"
        );
      }
      if (rightSection) {
        var rightStickyWrapperFade = rightSection.querySelectorAll(
          ".next-timeline--sticky-wrapper > *"
        );
      }
      var varScrub = nextTimeline.getAttribute("data-scrub");
      if (varScrub == "1") {
        varScrub = !0;
      } else {
        varScrub = !1;
      }
      var timeline = gsap.timeline({
        scrollTrigger: {
          trigger: item,
          start: nextTimeline.getAttribute("data-start"),
          end: nextTimeline.getAttribute("data-end"),
          scrub: varScrub,
          duration: parseFloat(nextTimeline.getAttribute("data-duration")),
          delay: parseFloat(nextTimeline.getAttribute("data-delay")),
          toggleActions: nextTimeline.getAttribute("data-toggle-actions"),
          easing: nextTimeline.getAttribute("data-easing"),
        },
      });
      if (leftStickyWrapperFade && enableLeftFade == "1") {
        timeline.to(leftStickyWrapperFade, { opacity: 1 }, 0);
      }
      if (centerStickyWrapperFade && enableCenterFade == "1") {
        timeline.to(centerStickyWrapperFade, { opacity: 1 }, 0);
      }
      if (rightStickyWrapperFade && enableRightFade == "1") {
        timeline.to(rightStickyWrapperFade, { opacity: 1 }, 0);
      }
    });
    var degree = nextTimeline.getAttribute("data-degree");
    var gradientColors = nextTimeline
      .getAttribute("data-gradient-colors")
      .split("bc11")
      .filter((color) => color && color !== "unset");
    if (gradientColors.length < 1) {
      gradientColors = ["#fff", "#1c6fe2", "orange"];
    }
    if (gradientColors.length === 1) {
      gradientColors.push(gradientColors[0]);
    }
    var gradient = `linear-gradient(${parseFloat(
      degree
    )}deg, ${gradientColors.join(", ")})`;
    nextTimeline.style.setProperty("--gradient", gradient);
  });
}
document.addEventListener("DOMContentLoaded", function () {
  bricksIsFrontend && next_timeline();
});
